------------------------------------------------ 聊天超鏈接增加物品等級 (支持大祕境鑰匙等級)-- @Author:M----------------------------------------------local B, C, L, DB = unpack(select(2, ...))local tooltip = CreateFrame("GameTooltip", "ChatLinkLevelTooltip", UIParent, "GameTooltipTemplate")local ItemLevelPattern = gsub(ITEM_LEVEL, "%%d", "(%%d+)")local ARMOR = ARMOR or "Armor"local WEAPON = WEAPON or "Weapon"local RELICSLOT = RELICSLOT or "Relic"--获取物品实际等级local function GetItemLevelAndTexture(ItemLink)	local itemName, _, itemRarity, itemLevel, itemMinLevel, itemType, itemSubType, itemStackCount, itemEquipLoc, itemTexture, itemSellPrice, itemClassID, itemSubClassID = GetItemInfo(ItemLink)	if (not itemTexture) then return end	local text, slotText	local level = NDui:GetItemLevel(ItemLink, itemRarity)	if itemEquipLoc and string.find(itemEquipLoc, "INVTYPE_") then		slotText = _G[itemEquipLoc] or ""	end	if itemEquipLoc and string.find(itemEquipLoc, "INVTYPE_FEET") then		slotText = L["Feet"] or ""	elseif itemEquipLoc and string.find(itemEquipLoc, "INVTYPE_HAND") then		slotText = L["Hands"] or ""	elseif itemEquipLoc and string.find(itemEquipLoc, "INVTYPE_HOLDABLE") then		slotText = INVTYPE_WEAPONOFFHAND or ""	elseif itemEquipLoc and string.find(itemEquipLoc, "INVTYPE_SHIELD") then		slotText = itemSubType or ""	elseif itemSubType and string.find(itemSubType, RELICSLOT) then		slotText = RELICSLOT or ""	elseif (itemClassID and itemClassID == 15) and (itemSubClassID and itemSubClassID == 2) then		slotText = PET or ""	elseif (itemClassID and itemClassID == 15) and (itemSubClassID and itemSubClassID == 5) then		slotText = itemSubType or ""	end	if (level and slotText) then		level = "<"..level.."-"..slotText..">"	elseif level then		level = "<"..level..">"	elseif slotText then		level = "<"..slotText..">"	else		level = ""	end	return level, itemTextureend--等级图标显示local function SetChatLinkLevel(Hyperlink)	local link = string.match(Hyperlink, "|H(.-)|h")	local level, itemTexture = GetItemLevelAndTexture(link)	if (level) then		Hyperlink = Hyperlink:gsub("|h%[(.-)%]|h", "|h[%1"..level.."]|h")	end	return Hyperlinkend--鑰匙等級local function SetChatLinkKeystoneLevel(Hyperlink)	local map, level, name = string.match(Hyperlink, "|Hkeystone:(%d+):(%d+):.-|h(.-)|h")	if (map and level and not string.find(name, level)) then		local name = C_ChallengeMode.GetMapInfo(map)		Hyperlink = Hyperlink:gsub("|h%[(.-)%]|h", "|h["..name.."+"..level.."]|h")	end	return Hyperlinkend--过滤器local function filter(self, event, msg, ...)	if (not TinyChatDB or not TinyChatDB.hideLinkLevel) then		msg = msg:gsub("(|Hitem:%d+:.-|h.-|h)", SetChatLinkLevel)	end	if (not TinyChatDB or not TinyChatDB.hideKeystoneLevel) then		msg = msg:gsub("(|Hkeystone:%d+:.-|h.-|h)", SetChatLinkKeystoneLevel)	end	return false, msg, ...endChatFrame_AddMessageEventFilter("CHAT_MSG_SAY", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_YELL", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_GUILD", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_GUILD_LEADER", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_OFFICER", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_CHANNEL", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_INSTANCE", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_INSTANCE_LEADER", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_PARTY", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_PARTY_LEADER", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_RAID", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_RAID_LEADER", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_WHISPER", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_WHISPER_INFORM", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_BN_WHISPER", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_BN_WHISPER_INFORM", filter)ChatFrame_AddMessageEventFilter("CHAT_MSG_LOOT", filter)