local B, C, L, DB = unpack(select(2, ...))local module = NDui:GetModule("Chat")function module:ChatCopy()	local lines = {}	local frame = CreateFrame("Frame", "NDuiChatCopy", UIParent)	frame:SetPoint("CENTER", UIParent, "CENTER")	frame:SetSize(700, 400)	frame:Hide()	frame:SetFrameStrata("DIALOG")	B.CreateMF(frame)	B.CreateBD(frame)	frame.close = CreateFrame("Button", nil, frame, "UIPanelCloseButton")	frame.close:SetPoint("TOPRIGHT", frame, "TOPRIGHT")	local scrollArea = CreateFrame("ScrollFrame", "ChatCopyScrollFrame", frame, "UIPanelScrollFrameTemplate")	scrollArea:SetPoint("TOPLEFT", frame, "TOPLEFT", 10, -30)	scrollArea:SetPoint("BOTTOMRIGHT", frame, "BOTTOMRIGHT", -30, 10)	local editBox = CreateFrame("EditBox", nil, frame)	editBox:SetMultiLine(true)	editBox:SetMaxLetters(99999)	editBox:EnableMouse(true)	editBox:SetAutoFocus(false)	editBox:SetFontObject(ChatFontNormal)	editBox:SetWidth(scrollArea:GetWidth())	editBox:SetHeight(270)	editBox:SetScript("OnEscapePressed", function(f) f:GetParent():GetParent():Hide() f:SetText("") end)	scrollArea:SetScrollChild(editBox)	local copyFunc = function()		local cf = SELECTED_DOCK_FRAME		local _, size = cf:GetFont()		FCF_SetChatWindowFontSize(cf, cf, .01)		local ct = 1		for i = select("#", cf.FontStringContainer:GetRegions()), 1, -1 do			local region = select(i, cf.FontStringContainer:GetRegions())			if region:GetObjectType() == "FontString" then				if region:GetText() ~= nil then					lines[ct] = tostring(region:GetText())					ct = ct + 1				end			end		end		local lineCt = ct - 1		local text = table.concat(lines, "\n", 1, lineCt)		FCF_SetChatWindowFontSize(cf, cf, size)		frame:Show()		editBox:SetText(text)		editBox:HighlightText(0)		wipe(lines)	end	local hintFunc = function(self)		B.CreateGT(self, "ANCHOR_TOP", L["Chat Copy"], "class")	end	for i = 1, 10 do		local tab = _G[format("%s%d%s", "ChatFrame", i, "Tab")]		tab:SetScript("OnDoubleClick", copyFunc)		tab:SetScript("OnEnter", hintFunc)	end	-- Aurora Reskin	if IsAddOnLoaded("Aurora") then		local F = unpack(Aurora)		F.ReskinClose(frame.close)		F.ReskinScroll(ChatCopyScrollFrameScrollBar)	endend